import C from '../../../actions/constants';

const initialState = {
    responseData: []
}

const dateConversion = (value) =>{

    let date = value.toString()
        let timestamp = date.substring(0, 13)
        var d = new Date(parseInt(timestamp))
        let dateStr = d.toISOString()
        var myDate = new Date(dateStr);
        let result = (myDate.toLocaleString('en-US', { day: '2-digit', month: '2-digit', year: 'numeric', hour: 'numeric', minute: 'numeric', second: 'numeric', hour12: true }))
    return result;
}

const getContributionList = (payload) => {
    let responseData = [];
    payload.map (item =>{
        let temp = []
        temp["sr_no"]        = item.submitRefNumber;
        temp["Dataset"]      = item.datasetName;
        temp["Submitted_on"] = dateConversion(item.submittedOn)
        temp["Status"]       = item.status
    })
    //   [{"sr_no":submitRefNumber,"Dataset":datasetName,"Submitted_on":submittedOn,"Status":status}]
    // let dataObj = [{"submitRefNumber":"0005770","datasetName":"Tourism Set-1","submittedOn":"23/5/2011","status":"Inprogress"},{"sr_no":"0045770","Dataset":"Tourism Set-7","Submitted_on":"3/5/2011","Status":"Published"},{"sr_no":"0205770","Dataset":"Tourism Set-5","Submitted_on":"12/5/2011","Status":"Published"},{"sr_no":"0005470","Dataset":"Tourism Set-4","Submitted_on":"2/5/2011","Status":"Published"}]
    // let latestEvent = removeDuplicates(result, 's_id')

    // return latestEvent;

    return [];
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case C.GET_CONTRIBUTION_LIST:
            return {
                responseData: getContributionList([{"submitRefNumber":"0005770","datasetName":"Tourism Set-1","submittedOn":"23/5/2011","status":"Published"},{"submitRefNumber":"0005771","datasetName":"Tourism Set-1","submittedOn":"23/5/2011","status":"Inprogress"},{"submitRefNumber":"0005772","datasetName":"Tourism Set-1","submittedOn":"23/5/2011","status":"Published"},{"submitRefNumber":"0005770","datasetName":"Tourism Set-1","submittedOn":"23/5/2011","status":"Published"}]),

            }
        case C.CLEAR_USER_EVENT:
            return {
                ...initialState
            }
        default:
            return {
                ...state
            }
    }
}

export default reducer;
