import { createMuiTheme } from "@material-ui/core/styles";



const themeDefault = createMuiTheme({
  typography: {
    fontFamily: '"Lato"',
    font: "normal normal normal 14px/17px Lato",
    letterSpacing: "0px",
    fontSize: 13,
  },
  overrides: {
    MuiButton: {
      label: {
        textTransform: "capitalize",
        fontWeight: "normal",
        lineHeight: "1.14",
        letterSpacing: "1.25px",
        textAlign: "center",
        height: "26px"
      },
    },
    MuiTableCell: {
      head    : {
              backgroundColor : "#c7c6c68a !important",

      }
},

MuiPaper: {
      root:{
              boxShadow       : 'none !important',
              borderRadius    : 0,
              border          : "1px solid rgb(224 224 224)"
      }
}
  },

  palette: {
    primary: {
      light: "#60568d",
      main: "#2C2799",
      dark: "#271e4f",
      contrastText: "#FFFFFF",
    },
    secondary: {
      light: "#000000",
      main: "#000000",
      dark: "#000000",
      contrastText: "#FFFFFF",
    },
    background: {
      default: "#2C2799",
    },
  },
});

themeDefault.typography.h4 = {
  fontSize: "1.875rem",
  fontWeight: "500",

  fontFamily: '"Poppins","lato" ,sans-serif',
  textAlign: "Left",
  "@media (min-width:600px)": {},
  [themeDefault.breakpoints.up("md")]: {},
};


export default themeDefault;
