import { Language, FilterBy } from '../configs/DatasetItems';
import DatasetItems from '../configs/DatasetItems';

export const FilterByDomain =(params, value  ) =>{
    let arr = []
    console.log(FilterBy.domain, value, params)

            FilterBy.domain.forEach(val => {
                if (val.value === value)
                    arr.push(val.label)
            })
            console.log(arr)
            return arr;


}

export const FilterByCollection =(params, value  ) =>{
    let arr = []
    console.log(FilterBy.domain, value, params)

            FilterBy.collectionMethod.forEach(val => {
                if (val.value === value)
                    arr.push(val.label)
            })
            console.log(arr)
            return arr;


}


 export const getLanguageLabel= (value, params = 'language') => {
    let arr = []
    switch (params) {
        case 'datasetType':
            DatasetItems.forEach(val => {
                if (val.value === value)
                    arr.push(val.label)
            })
            return arr;
        default:
            Language.forEach(val => {
                value.forEach(data => {
                    if (val.value === data)
                        arr.push(val)
                })

            })
            return arr
    }

}

export default getLanguageLabel;
