import { Language, FilterBy } from '../configs/DatasetItems';
import DatasetItems from '../configs/DatasetItems';

export const FilterBySearch =(value, params ) =>{
    let arr = []
    switch (params) {
        case 'collectionMethod':
            FilterBy.domain.forEach(val => {
                if (val.value === value)
                    arr.push(val.label)
            })
            return arr;
        default:
            FilterBy.collectionMethod.forEach(val => {
                value.forEach(data => {
                    if (val.value === data)
                        arr.push(val)
                })

            })
            return arr
    }

}


 export const getLanguageLabel= (value, params = 'language') => {
    let arr = []
    switch (params) {
        case 'datasetType':
            DatasetItems.forEach(val => {
                if (val.value === value)
                    arr.push(val.label)
            })
            return arr;
        default:
            Language.forEach(val => {
                value.forEach(data => {
                    if (val.value === data)
                        arr.push(val)
                })

            })
            return arr
    }

}

export default getLanguageLabel;
